import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { increase, decrease, remove } from "./CartSlice";

function CartItem(props) {
  const dispatch = useDispatch();

  return (
    <>
      <div className="cart-item-container">
        <img src={props.image} width="100px" className="cart-item-img" />
        <h2 className="cart-item-title">{props.title}</h2>

        <h3>{props.weight}</h3>
        <h3>{props.price}</h3>
        <button onClick={() => dispatch(remove(props.id))}>Remove</button>

        <button onClick={() => dispatch(decrease(props.id))}>-</button>
        <h3>{props.quantity}</h3>
        <button onClick={() => dispatch(increase(props.id))}>+</button>
      </div>
    </>
  );
}

export default CartItem;
