import React, { useState } from "react";
import { useParams } from "react-router";
import Database from "./Database";
import { useDispatch } from "react-redux";
import { increase, decrease } from "./redux/CartSlice";

function ProductPage() {
  const { id } = useParams();
  const dispatch = useDispatch();
  const [counter, setCounter] = useState(Database[id - 1].quantity);

  function addProduct() {
    setCounter((prevState) => prevState + 1);
  }

  function removeProduct() {
    setCounter((prevState) => prevState - 1);
  }

  if (counter < 1) {
    setCounter(1);
  }

  return (
    <>
      <div className="product-container">
        <div>
          <img
            className="product-page-img"
            src={Database[id - 1].image}
            alt={Database[id - 1].title}
          />
        </div>
        <div className="product-page-description">
          <h1>{Database[id - 1].title}</h1>
          <h3>{Database[id - 1].description}</h3>
          <h3>Â£{Database[id - 1].price}</h3>
          <div className="qty-container">
            <button onClick={removeProduct} className="qty-btn">
              -
            </button>
            <h2>{counter}</h2>
            <button onClick={addProduct} className="qty-btn">
              +
            </button>
          </div>
          <button className="buy-btn">ADD TO CART</button>
        </div>
      </div>
    </>
  );
}

export default ProductPage;
