import { createSlice } from "@reduxjs/toolkit";
import Database from "../Database";

const initialState = {
  cartItems: Database,
  cartTotalQuantity: 0,
  cartTotalAmount: 0,
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    getCarttotal: (state) => {
      let { cartTotalAmount, cartTotalQuantity } = state.cartItems.reduce;
    },
  },
});

export default cartSlice.reducer;
